validation_errors.pattern:
try:
    responses = manager.execute_batch(template)
except ValueError as e:
    print(f'Validation error: {e}')
    # Handle error - maybe retry with default values

user_cancellation.pattern:
# Check if user cancelled responses
for response in responses.values():
    if response.metadata.get('cancelled'):
        print('User cancelled question flow')
        break
